unconstrained fn get_shuffle_indices<T, let N: u32>(
    lhs: [T; N],
    rhs: [T; N]
) -> [Field; N] where T: std::cmp::Eq {
    let mut shuffle_indices: [Field;N ] = [0; N];

    let mut shuffle_mask: [bool; N] = [false; N];
    for i in 0..N {
        let mut found = false;
        for j in 0..N {
            if (shuffle_mask[j] == false) {
                if (lhs[i] == rhs[j]) {
                    found = true;
                    shuffle_indices[i] = j as Field;
                    shuffle_mask[j] = true;
                }
            }
            if (found) {
                break;
            }
        }
        assert(found == true, "check_shuffle, lhs and rhs arrays do not contain equivalent values");
    }

    shuffle_indices
}

pub fn check_shuffle<T, let N: u32>(lhs: [T; N], rhs: [T; N]) where T: std::cmp::Eq {
    let shuffle_indices = get_shuffle_indices(lhs, rhs);
    let mut index_masks: [Field; N] = [0; N];

    for i in 0..N {
        let idx = shuffle_indices[i];
        assert_eq(index_masks[idx], 0);
        index_masks[idx] = 1;
        let expected = rhs[idx];
        let result = lhs[i];
        assert_eq(expected, result);
    }
}

mod test {
    struct CompoundStruct {
        a: bool,
        b: Field,
        c: u64
    }
    impl std::cmp::Eq for CompoundStruct {
        fn eq(self, other: Self) -> bool {
            (self.a == other.a) & (self.b == other.b) & (self.c == other.c)
        }
    }

    use crate::check_shuffle;
    #[test]
    fn test_shuffle() {
        let lhs: [Field; 5] = [0, 1, 2, 3, 4];
        let rhs: [Field; 5] = [2, 0, 3, 1, 4];
        check_shuffle(lhs, rhs);
    }

    #[test]
    fn test_shuffle_identity() {
        let lhs: [Field; 5] = [0, 1, 2, 3, 4];
        let rhs: [Field; 5] = [0, 1, 2, 3, 4];
        check_shuffle(lhs, rhs);
    }

    #[test(should_fail_with = "check_shuffle, lhs and rhs arrays do not contain equivalent values")]
    fn test_shuffle_fail() {
        let lhs: [Field; 5] = [0, 1, 2, 3, 4];
        let rhs: [Field; 5] = [0, 1, 2, 3, 5];
        check_shuffle(lhs, rhs);
